CREATE TABLE IF NOT EXISTS Issue(iID VARCHAR(36) PRIMARY KEY NOT NULL, user VARCHAR(30) NOT NULL, name VARCHAR(300) NOT NULL, description TEXT NOT NULL, solution TEXT NOT NULL, attempts TEXT, FOREIGN KEY (user) REFERENCES users(username));
CREATE TABLE IF NOT EXISTS Tag(iID INT NOT NULL, tag VARCHAR(50) NOT NULL, FOREIGN KEY (iID) REFERENCES issues(iID), CONSTRAINT pk_tags PRIMARY KEY (iID, tag));
CREATE TABLE IF NOT EXISTS User(username VARCHAR(30) PRIMARY KEY NOT NULL, password CHAR(64) NOT NULL, timeCreated DATETIME NOT NULL);